import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Paper,
  Grid,
  Alert,
  Chip,
  Stack,
} from '@mui/material';
import {
  Assignment as TaskIcon,
  LibraryBooks as ResourceIcon,
  Group as TeamIcon,
  Work as ProjectIcon,
  Hardware as HardwareIcon,
  Code as CodeIcon,
  Warning as WarningIcon,
  CheckCircle as SuccessIcon,
  Error as ErrorIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

interface StatCardProps {
  title: string;
  value: string | number;
  description: string;
  link: string;
  icon: React.ReactNode;
  color: string;
}

interface KpiCardProps {
  title: string;
  value: number;
  target: number;
  unit: string;
  trend: number;
  icon: React.ReactNode;
  color: string;
}

const StatCard: React.FC<StatCardProps> = ({ title, value, description, link, icon, color }) => {
  const navigate = useNavigate();
  const { t } = useLanguage();

  return (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
          {icon}
          <Typography color="textSecondary" gutterBottom>
            {t(title)}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ color }}>
          {value}
        </Typography>
        <Typography variant="body2" color="textSecondary">
          {t(description)}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={() => navigate(link)}>
          {t('viewDetails')}
        </Button>
      </CardActions>
    </Card>
  );
};

const KpiCard: React.FC<KpiCardProps> = ({ title, value, target, unit, trend, icon, color }) => {
  const { t } = useLanguage();
  const progress = (value / target) * 100;
  const trendColor = trend >= 0 ? '#4caf50' : '#f44336';

  return (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
          {icon}
          <Typography color="textSecondary">
            {t(title)}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ color }}>
          {value}{unit}
        </Typography>
        <Box sx={{ mt: 1 }}>
          <Typography variant="body2" color="textSecondary">
            {t('target')}: {target}{unit}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Typography variant="body2" sx={{ color: trendColor }}>
              {trend >= 0 ? '+' : ''}{trend}%
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {t('vsLastPeriod')}
            </Typography>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

const Dashboard: React.FC = () => {
  const { t } = useLanguage();

  // 任务完成趋势数据
  const taskCompletionData = [
    { name: '1月', completed: 12, total: 15 },
    { name: '2月', completed: 15, total: 18 },
    { name: '3月', completed: 18, total: 20 },
    { name: '4月', completed: 20, total: 22 },
    { name: '5月', completed: 22, total: 25 },
  ];

  // 资源使用分布数据
  const resourceUsageData = [
    { name: '文档', value: 35 },
    { name: '代码', value: 25 },
    { name: '设计', value: 20 },
    { name: '其他', value: 20 },
  ];

  // 团队活跃度数据
  const teamActivityData = [
    { name: '周一', commits: 12, reviews: 8 },
    { name: '周二', commits: 15, reviews: 10 },
    { name: '周三', commits: 18, reviews: 12 },
    { name: '周四', commits: 14, reviews: 9 },
    { name: '周五', commits: 16, reviews: 11 },
  ];

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

  const stats = [
    {
      title: 'totalTasks',
      value: 24,
      description: 'activeDevelopmentTasks',
      link: '/development-platform',
      icon: <TaskIcon sx={{ color: '#2196f3' }} />,
      color: '#2196f3',
    },
    {
      title: 'resources',
      value: 156,
      description: 'availableResources',
      link: '/resources',
      icon: <ResourceIcon sx={{ color: '#4caf50' }} />,
      color: '#4caf50',
    },
    {
      title: 'teamMembers',
      value: 12,
      description: 'activeTeamMembers',
      link: '/team',
      icon: <TeamIcon sx={{ color: '#ff9800' }} />,
      color: '#ff9800',
    },
    {
      title: 'projects',
      value: 8,
      description: 'ongoingProjects',
      link: '/projects',
      icon: <ProjectIcon sx={{ color: '#9c27b0' }} />,
      color: '#9c27b0',
    },
  ];

  // 硬件开发指标数据
  const hardwareKpis = [
    {
      title: 'schemeReviewRate',
      value: 85,
      target: 90,
      unit: '%',
      trend: 5,
      icon: <HardwareIcon sx={{ color: '#2196f3' }} />,
      color: '#2196f3',
    },
    {
      title: 'pcbSuccessRate',
      value: 92,
      target: 95,
      unit: '%',
      trend: 3,
      icon: <HardwareIcon sx={{ color: '#4caf50' }} />,
      color: '#4caf50',
    },
    {
      title: 'testCoverage',
      value: 88,
      target: 95,
      unit: '%',
      trend: -2,
      icon: <HardwareIcon sx={{ color: '#ff9800' }} />,
      color: '#ff9800',
    },
  ];

  // 软件开发指标数据
  const softwareKpis = [
    {
      title: 'codeReviewRate',
      value: 95,
      target: 100,
      unit: '%',
      trend: 2,
      icon: <CodeIcon sx={{ color: '#2196f3' }} />,
      color: '#2196f3',
    },
    {
      title: 'testCoverage',
      value: 82,
      target: 90,
      unit: '%',
      trend: 5,
      icon: <CodeIcon sx={{ color: '#4caf50' }} />,
      color: '#4caf50',
    },
    {
      title: 'bugFixRate',
      value: 78,
      target: 85,
      unit: '%',
      trend: -3,
      icon: <CodeIcon sx={{ color: '#ff9800' }} />,
      color: '#ff9800',
    },
  ];

  // 预警指标数据
  const warnings = [
    {
      type: 'error',
      message: 'hardwareDesignRisk',
      details: 'pcbDesignDelay',
    },
    {
      type: 'warning',
      message: 'softwareQualityRisk',
      details: 'testCoverageLow',
    },
    {
      type: 'info',
      message: 'projectProgressRisk',
      details: 'milestoneDelay',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        {t('dashboard')}
      </Typography>

      {/* 预警指标 */}
      <Box sx={{ mb: 4 }}>
        <Stack spacing={2}>
          {warnings.map((warning, index) => (
            <Alert
              key={index}
              severity={warning.type as 'error' | 'warning' | 'info'}
              icon={warning.type === 'error' ? <ErrorIcon /> : warning.type === 'warning' ? <WarningIcon /> : <SuccessIcon />}
            >
              <Typography variant="subtitle1">{t(warning.message)}</Typography>
              <Typography variant="body2">{t(warning.details)}</Typography>
            </Alert>
          ))}
        </Stack>
      </Box>

      {/* 统计卡片 */}
      <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr', md: '1fr 1fr 1fr 1fr' }, gap: 3, mb: 4 }}>
        {stats.map((stat) => (
          <Box key={stat.title}>
            <StatCard {...stat} />
          </Box>
        ))}
      </Box>

      {/* 关键指标卡片 */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          {t('hardwareDevelopmentKpis')}
        </Typography>
        <Grid container spacing={3}>
          {hardwareKpis.map((kpi, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <KpiCard {...kpi} />
            </Grid>
          ))}
        </Grid>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          {t('softwareDevelopmentKpis')}
        </Typography>
        <Grid container spacing={3}>
          {softwareKpis.map((kpi, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <KpiCard {...kpi} />
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* 图表区域 */}
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* 第一行：任务完成趋势和资源使用分布 */}
        <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', md: 'row' } }}>
          {/* 任务完成趋势图 */}
          <Box sx={{ flex: 2 }}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {t('taskCompletionTrend')}
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={taskCompletionData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="completed" stroke="#2196f3" name={t('completedTasks')} />
                  <Line type="monotone" dataKey="total" stroke="#4caf50" name={t('totalTasks')} />
                </LineChart>
              </ResponsiveContainer>
            </Paper>
          </Box>

          {/* 资源使用分布图 */}
          <Box sx={{ flex: 1 }}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {t('resourceUsage')}
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={resourceUsageData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {resourceUsageData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </Paper>
          </Box>
        </Box>

        {/* 团队活跃度图 */}
        <Box>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              {t('teamActivity')}
            </Typography>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={teamActivityData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="commits" fill="#2196f3" name={t('commits')} />
                <Bar dataKey="reviews" fill="#4caf50" name={t('reviews')} />
              </BarChart>
            </ResponsiveContainer>
          </Paper>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
