import React, { useState } from 'react';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  CardActions,
  TextField,
  MenuItem,
  Chip,
  Select,
  FormControl,
  Menu,
  Stack,
} from '@mui/material';
import {
  FilterList as FilterIcon,
  LibraryBooks as LibraryIcon,
  Code as CodeIcon,
  Build as BuildIcon,
  Science as ScienceIcon,
  Handshake as HandshakeIcon,
} from '@mui/icons-material';
import { useLanguage } from '../../contexts/LanguageContext';
import { SelectChangeEvent } from '@mui/material';

interface Resource {
  _id: string;
  title: string;
  description: string;
  type: string;
  status: string;
  createdAt: string;
}

// Mock data for development
const mockResources: Resource[] = [
  {
    _id: '1',
    title: 'React Best Practices',
    description: 'A comprehensive guide to React development best practices',
    type: 'document',
    status: 'published',
    createdAt: '2024-03-15',
  },
  {
    _id: '2',
    title: 'TypeScript Handbook',
    description: 'Official TypeScript documentation and examples',
    type: 'document',
    status: 'published',
    createdAt: '2024-03-14',
  },
  {
    _id: '3',
    title: 'API Integration Guide',
    description: 'Step-by-step guide for integrating REST APIs',
    type: 'code',
    status: 'draft',
    createdAt: '2024-03-13',
  },
  {
    _id: '4',
    title: 'UI Component Library',
    description: 'Reusable UI components for rapid development',
    type: 'code',
    status: 'inReview',
    createdAt: '2024-03-12',
  },
  {
    _id: '5',
    title: 'Performance Optimization Tools',
    description: 'Tools and techniques for optimizing application performance',
    type: 'tool',
    status: 'published',
    createdAt: '2024-03-11',
  },
  {
    _id: '6',
    title: 'Testing Framework Setup',
    description: 'Complete guide to setting up testing frameworks',
    type: 'tool',
    status: 'archived',
    createdAt: '2024-03-10',
  },
];

const ResourceLibrary: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedType, setSelectedType] = useState('all');
  const [showFilters, setShowFilters] = useState(false);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [resources] = useState<Resource[]>(mockResources);
  const { t, language, setLanguage } = useLanguage();

  const handleFilterClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleFilterClose = () => {
    setAnchorEl(null);
  };

  const handleTypeSelect = (type: string) => {
    setSelectedType(type);
    handleFilterClose();
  };

  const handleLanguageChange = (event: SelectChangeEvent) => {
    setLanguage(event.target.value as 'en' | 'zh');
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'document':
        return <LibraryIcon sx={{ color: '#2196f3' }} />;
      case 'code':
        return <CodeIcon sx={{ color: '#4caf50' }} />;
      case 'tool':
        return <BuildIcon sx={{ color: '#ff9800' }} />;
      default:
        return <LibraryIcon sx={{ color: '#2196f3' }} />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'published':
        return 'success';
      case 'draft':
        return 'warning';
      case 'archived':
        return 'error';
      default:
        return 'default';
    }
  };

  const filteredResources = resources.filter((resource: Resource) => {
    const matchesSearch = resource.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      resource.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = selectedType === 'all' || resource.type === selectedType;
    return matchesSearch && matchesType;
  });

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          {t('resourceLibrary')}
        </Typography>
        <FormControl sx={{ minWidth: 120 }}>
          <Select
            value={language}
            onChange={handleLanguageChange}
            size="small"
          >
            <MenuItem value="en">English</MenuItem>
            <MenuItem value="zh">中文</MenuItem>
          </Select>
        </FormControl>
      </Box>

      <Box sx={{ mb: 3, display: 'flex', gap: 2, alignItems: 'center' }}>
        <TextField
          label={t('search')}
          variant="outlined"
          size="small"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          sx={{ flexGrow: 1 }}
        />
        <Button
          variant="outlined"
          startIcon={<FilterIcon sx={{ color: '#9c27b0' }} />}
          onClick={handleFilterClick}
        >
          {t('filter')}
        </Button>
      </Box>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleFilterClose}
      >
        <MenuItem onClick={() => handleTypeSelect('all')}>
          {t('allTypes')}
        </MenuItem>
        <MenuItem onClick={() => handleTypeSelect('document')}>
          <LibraryIcon sx={{ color: '#2196f3', mr: 1 }} />
          {t('document')}
        </MenuItem>
        <MenuItem onClick={() => handleTypeSelect('code')}>
          <CodeIcon sx={{ color: '#4caf50', mr: 1 }} />
          {t('code')}
        </MenuItem>
        <MenuItem onClick={() => handleTypeSelect('tool')}>
          <BuildIcon sx={{ color: '#ff9800', mr: 1 }} />
          {t('tool')}
        </MenuItem>
      </Menu>

      <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 3 }}>
        {filteredResources.map((resource: Resource) => (
          <Card key={resource._id}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {getTypeIcon(resource.type)}
                  <Typography variant="h6" component="div">
                    {resource.title}
                  </Typography>
                </Box>
              </Box>
              <Typography color="text.secondary" sx={{ mt: 1 }}>
                {resource.description}
              </Typography>
              <Stack direction="row" spacing={1} sx={{ mt: 2 }}>
                <Chip 
                  label={t(resource.type)} 
                  size="small"
                  icon={getTypeIcon(resource.type)}
                />
                <Chip 
                  label={t(`status.${resource.status}`)} 
                  size="small"
                  color={getStatusColor(resource.status)}
                />
              </Stack>
            </CardContent>
            <CardActions>
              <Button size="small">{t('viewDetails')}</Button>
            </CardActions>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default ResourceLibrary;
