import React from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  LinearProgress,
  Grid,
  Chip,
} from '@mui/material';
import { CheckCircle, Schedule, Pending } from '@mui/icons-material';

interface Task {
  id: string;
  title: string;
  status: 'completed' | 'in-progress' | 'pending';
  startDate: string;
  endDate: string;
  progress?: number;
  description?: string;
}

const TaskProgress: React.FC = () => {
  const tasks: Task[] = [
    {
      id: '1',
      title: '实现数据统计和可视化',
      status: 'completed',
      startDate: '2025-05-19',
      endDate: '2025-05-19',
      description: '使用 Recharts 实现数据可视化，添加各类统计图表'
    },
    {
      id: '2',
      title: '完善资源上传和管理功能',
      status: 'completed',
      startDate: '2025-05-20',
      endDate: '2025-05-20',
      description: '实现文件上传、下载和管理功能'
    },
    {
      id: '3',
      title: '实现资源分类系统',
      status: 'in-progress',
      startDate: '2025-05-20',
      endDate: '2025-05-22',
      progress: 0,
      description: '实现多级分类、标签系统和分类管理功能'
    },
    {
      id: '4',
      title: '实现预警功能',
      status: 'in-progress',
      startDate: '2025-05-19',
      endDate: '2025-05-20',
      progress: 50,
      description: '实现系统预警和通知功能'
    }
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'success';
      case 'in-progress':
        return 'primary';
      case 'pending':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle />;
      case 'in-progress':
        return <Schedule />;
      case 'pending':
        return <Pending />;
      default:
        return null;
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        任务进度追踪
      </Typography>
      <Grid container spacing={3}>
        {tasks.map((task) => (
          <Grid item xs={12} md={6} key={task.id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6">{task.title}</Typography>
                  <Chip
                    icon={getStatusIcon(task.status)}
                    label={task.status === 'in-progress' ? '进行中' : task.status === 'completed' ? '已完成' : '待开始'}
                    color={getStatusColor(task.status) as 'success' | 'primary' | 'warning' | 'default'}
                    size="small"
                  />
                </Box>
                <Typography color="textSecondary" gutterBottom>
                  开始日期: {task.startDate} | 预计完成: {task.endDate}
                </Typography>
                {task.description && (
                  <Typography variant="body2" color="textSecondary" sx={{ mb: 2 }}>
                    {task.description}
                  </Typography>
                )}
                {task.status === 'in-progress' && task.progress !== undefined && (
                  <Box sx={{ mt: 2 }}>
                    <LinearProgress
                      variant="determinate"
                      value={task.progress}
                      sx={{ height: 8, borderRadius: 4 }}
                    />
                    <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                      完成进度: {task.progress}%
                    </Typography>
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default TaskProgress;
