import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Paper,
  Grid,
  Alert,
  Chip,
  Stack,
} from '@mui/material';
import {
  Assignment as TaskIcon,
  LibraryBooks as ResourceIcon,
  Group as TeamIcon,
  Work as ProjectIcon,
  Hardware as HardwareIcon,
  Code as CodeIcon,
  Warning as WarningIcon,
  CheckCircle as SuccessIcon,
  Error as ErrorIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

interface StatCardProps {
  title: string;
  value: string | number;
  description: string;
  link: string;
  icon: React.ReactNode;
  color: string;
}

interface KpiCardProps {
  title: string;
  value: number;
  target: number;
  unit: string;
  trend: number;
  icon: React.ReactNode;
  color: string;
}

const StatCard: React.FC<StatCardProps> = ({ title, value, description, link, icon, color }) => {
  const navigate = useNavigate();
  const { t } = useLanguage();

  return (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
          {icon}
          <Typography color="textSecondary" gutterBottom>
            {t(title)}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ color }}>
          {value}
        </Typography>
        <Typography variant="body2" color="textSecondary">
          {t(description)}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={() => navigate(link)}>
          {t('viewDetails')}
        </Button>
      </CardActions>
    </Card>
  );
};

const KpiCard: React.FC<KpiCardProps> = ({ title, value, target, unit, trend, icon, color }) => {
  const { t } = useLanguage();
  const progress = (value / target) * 100;
  const trendColor = trend >= 0 ? '#4caf50' : '#f44336';

  return (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
          {icon}
          <Typography color="textSecondary">
            {t(title)}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ color }}>
          {value}{unit}
        </Typography>
        <Box sx={{ mt: 1 }}>
          <Typography variant="body2" color="textSecondary">
            {t('target')}: {target}{unit}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Typography variant="body2" sx={{ color: trendColor }}>
              {trend >= 0 ? '+' : ''}{trend}%
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {t('vsLastPeriod')}
            </Typography>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

interface DashboardData {
  stats: {
    totalTasks: number;
    resources: number;
    teamMembers: number;
    projects: number;
  };
  hardwareKpis: Array<{
    title: string;
    value: number;
    target: number;
    unit: string;
    trend: number;
    color: string;
  }>;
  softwareKpis: Array<{
    title: string;
    value: number;
    target: number;
    unit: string;
    trend: number;
    color: string;
  }>;
  warnings: Array<{
    type: 'error' | 'warning' | 'info';
    message: string;
    details: string;
  }>;
  taskCompletionData: Array<{
    name: string;
    completed: number;
    total: number;
  }>;
  resourceUsageData: Array<{
    name: string;
    value: number;
  }>;
  teamActivityData: Array<{
    name: string;
    commits: number;
    reviews: number;
  }>;
}

const Dashboard: React.FC = () => {
  const { t } = useLanguage();
  const [data, setData] = useState<DashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/dashboard');
        if (!response.ok) {
          throw new Error('Failed to fetch dashboard data');
        }
        const jsonData = await response.json();
        setData(jsonData);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'An error occurred');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography>Loading...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  if (!data) {
    return null;
  }

  const stats = [
    {
      title: 'totalTasks',
      value: data.stats.totalTasks,
      description: 'activeDevelopmentTasks',
      link: '/development-platform',
      icon: <TaskIcon sx={{ color: '#2196f3' }} />,
      color: '#2196f3',
    },
    {
      title: 'resources',
      value: data.stats.resources,
      description: 'availableResources',
      link: '/resources',
      icon: <ResourceIcon sx={{ color: '#4caf50' }} />,
      color: '#4caf50',
    },
    {
      title: 'teamMembers',
      value: data.stats.teamMembers,
      description: 'activeTeamMembers',
      link: '/team',
      icon: <TeamIcon sx={{ color: '#ff9800' }} />,
      color: '#ff9800',
    },
    {
      title: 'projects',
      value: data.stats.projects,
      description: 'ongoingProjects',
      link: '/projects',
      icon: <ProjectIcon sx={{ color: '#9c27b0' }} />,
      color: '#9c27b0',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        {t('dashboard')}
      </Typography>

      {/* 预警指标 */}
      <Box sx={{ mb: 4 }}>
        <Stack spacing={2}>
          {data.warnings.map((warning, index) => (
            <Alert
              key={index}
              severity={warning.type}
              icon={warning.type === 'error' ? <ErrorIcon /> : warning.type === 'warning' ? <WarningIcon /> : <SuccessIcon />}
            >
              <Typography variant="subtitle1">{t(warning.message)}</Typography>
              <Typography variant="body2">{t(warning.details)}</Typography>
            </Alert>
          ))}
        </Stack>
      </Box>

      {/* 统计卡片 */}
      <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr', md: '1fr 1fr 1fr 1fr' }, gap: 3, mb: 4 }}>
        {stats.map((stat) => (
          <Box key={stat.title}>
            <StatCard {...stat} />
          </Box>
        ))}
      </Box>

      {/* 关键指标卡片 */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          {t('hardwareDevelopmentKpis')}
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr', md: '1fr 1fr 1fr' }, gap: 3 }}>
          {data.hardwareKpis.map((kpi, index) => (
            <Box key={index}>
              <KpiCard {...kpi} icon={<HardwareIcon sx={{ color: kpi.color }} />} color={kpi.color} />
            </Box>
          ))}
        </Box>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          {t('softwareDevelopmentKpis')}
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr', md: '1fr 1fr 1fr' }, gap: 3 }}>
          {data.softwareKpis.map((kpi, index) => (
            <Box key={index}>
              <KpiCard {...kpi} icon={<CodeIcon sx={{ color: kpi.color }} />} color={kpi.color} />
            </Box>
          ))}
        </Box>
      </Box>

      {/* 图表区域 */}
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* 第一行：任务完成趋势和资源使用分布 */}
        <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', md: 'row' } }}>
          {/* 任务完成趋势图 */}
          <Box sx={{ flex: 2 }}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {t('taskCompletionTrend')}
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={data.taskCompletionData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="completed" stroke="#2196f3" name={t('completedTasks')} />
                  <Line type="monotone" dataKey="total" stroke="#4caf50" name={t('totalTasksChart')} />
                </LineChart>
              </ResponsiveContainer>
            </Paper>
          </Box>

          {/* 资源使用分布图 */}
          <Box sx={{ flex: 1 }}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {t('resourceUsage')}
              </Typography>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={data.resourceUsageData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {data.resourceUsageData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </Paper>
          </Box>
        </Box>

        {/* 团队活跃度图 */}
        <Box>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              {t('teamActivity')}
            </Typography>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={data.teamActivityData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="commits" fill="#2196f3" name={t('commits')} />
                <Bar dataKey="reviews" fill="#4caf50" name={t('reviews')} />
              </BarChart>
            </ResponsiveContainer>
          </Paper>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
